// Simple Gong webhook processor
const input = $input.first().json;
const timestamp = new Date().toISOString();

const eventType = input.eventType || 'unknown';
const callId = input.callId || 'no-id';

console.log(`[${timestamp}] Gong ${eventType} for call ${callId}`);

return {
  eventType,
  callId,
  timestamp,
  processed: true,
  data: input
};
